<script type="module">
import { gsap } from "gsap/dist/gsap.js";
import { MorphSVGPlugin } from "gsap/dist/MorphSVGPlugin.js";

export default {
  mounted() {
    gsap.registerPlugin(MorphSVGPlugin)
    const tl = gsap.timeline({
      defaults: {duration: 1}
    })
    const tentacleStart = document.getElementById("tentacle-start")

    tl.add('morphit')
    tl.to(tentacleStart, {onUpdate:updateSlider,morphSVG:"#tentacle-end"}, 'morphit')

    for (let i = 1; i <= 19; i++) {
      const suckerTopStart = document.getElementById("sucker-top-start-"+i)
      const suckerBaseStart = document.getElementById("sucker-base-start-"+i)
      tl.to(suckerTopStart, {onUpdate: updateSlider, morphSVG:"#sucker-top-end-"+i }, 'morphit')
      tl.to(suckerBaseStart, {onUpdate: updateSlider, morphSVG:"#sucker-base-end-"+i }, 'morphit')
    }

    tl.to(tentacleStart, {onUpdate: updateSlider, opacity:0, duration: 1}, 'morphit+1',)

    // document.getElementById("slider").slider({
    //   range: false,
    //   min: 0,
    //   max: 1,
    //   step:.001,
    //   slide: function ( event, ui ) {
    //     tl.progress( ui.value ).pause();
    //   },
    //   stop: function () {
    //     tl.play();
    //   }
    // });	

    function updateSlider() {
    //   document.getElementById("slider").slider("value", tl.progress());
    }
  }
}
</script>

